import { useEffect, useState } from "react";
import PortfolioList from "../portfolioList/PortfolioList";
import "./portfolio.scss";
import {
  featuredPortfolio,
  IntrodcutionPortfolio,
  educationPortfolio,
  achievementsPortfolio,
  conclusionPortfolio,
} from "../../data";

export default function Portfolio() {
  const [selected,setSelected] = useState("quote");
  const [data,setData] = useState([]);
  const list = [
    {
      id: "quote",
      title: "Quote",
    },
    {
      id: "introduction",
      title: "Introduction",
    },
    {
      id: "education",
      title: "Education",
    },
    {
      id: "achievements",
      title: "Achievements",
    },
    {
      id: "conclusion",
      title: "Conclusion",
    },
  ];

  useEffect(()=>{

    switch(selected){
      case "featured": 
        setData(featuredPortfolio);
        break;
      case "introduction": 
        setData(IntrodcutionPortfolio);
        break;
      case "education": 
        setData(educationPortfolio);
        break;
      case "achievements": 
        setData(achievementsPortfolio);
        break;
      case "conclusion": 
        setData(conclusionPortfolio);
        break;
      default:
          setData(featuredPortfolio);
    }

  },[selected])


  return (
    <div className="portfolio" id="portfolio">
      <h1>Portfolio</h1>
      <ul>
        {list.map(item=> (
          <PortfolioList 
            title={item.title}
            active={selected === item.id} 
            setSelected={setSelected}
            id={item.id}
          />
        ))}
      </ul>
      <div className="container">
        {data.map(d=>(

          <div className="item">
            <img
              src={d.img}
              alt=""
            />
            <h3>{d.title}</h3>
          </div>
        ))}
      </div>
    </div>
  )
}
